import {useState} from 'react';
import Layout from '../components/layout';

const Register = () => {

    // using hooks
    // array destructuring
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        // if two user try to register with same email then we will give error
        error: '',
        // after registering we will give success msg
        success: '',
        // we will change dynamically button text
        buttonText: 'Register'

    })

    const registerForm = () => <form>
        <div className="form-group">
        <label for="">UserName</label>
            <input type="text" className="form-control" placeholder="Type Your name"/>
        </div>
        <div className="form-group">
        <label for="">E-mail</label>
            <input type="email" className="form-control" placeholder="Type Your email"/>
        </div>
        <div className="form-group">
        <label for="">Password</label>
            <input type="password" className="form-control" placeholder="Type Your password"/>
        </div>
        <div className="form-group">
          <button className="btn btn-outline-warning">Register</button>
        </div>
    </form>
    return ( 
    <Layout>
        <div className="col-md-6 offset-md-3">
            <h1>Register</h1>
            <br />
            {registerForm()}
        </div>
    </Layout>
    );
};

export default Register;
