import {useState} from 'react';
import Layout from '../components/layout';
// import '../public/static/css/styles.css';



const Register = () => {

    // using hooks
    // array destructuring
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        // if two user try to register with same email then we will give error
        error: '',
        // after registering we will give success msg
        success: '',
        // we will change dynamically button text
        buttonText: 'Register Me'

    });

    // now we use directly like buttonText in form where buttn created
    const {name, email, password, error, success, buttonText} = state;

// using function inside another function e is an event
    const handleChange = (name) => (e) => {
        setState({...state, [name]: e.target.value, error: '', success: '', buttonText: 'Register Me'})
    };

const handleSubmit = e => {
    // to prevent reloading of page
    e.preventDefault();
    console.table(name, email, password);
}
    

    const registerForm = () => 
    <form onSubmit = {handleSubmit}>

<div className="container">
        <div className="row">
            <div className="col-md-10 offset=md-1">
                <div className="row">
                    <div className="col-md-5 register-left">
                        <img src={require('./register.jpg')} alt=""></img>
                        <h3>Join Us</h3>
                        <p>Subscribe.......</p>
                       <button type="button" className="btn btn-primary">About</button> 
                    </div>

                    <div className="col-md-7 register-right">
                        <h2>Register Here</h2>
                        <div className="register-form">

                            <div className="form-group">
                                <input type="text " className="input form-control" placeholder="Username"></input>

                            </div>
                            <div className="form-group">
                                <input type="email " className="input form-control" placeholder="Email"></input>
                            </div>
                            <div className="form-group">
                                <input type="password" className="input form-control" placeholder="Password"></input>
                            </div>
                            <button type="button" className="btn btn-primary">Register</button>
                        </div>
                    </div>

                </div>
            </div>
        </div>
    </div>
    </form>

    return ( 
    <Layout>
        <div className="full col-md-6 offset-md-3">
            <h1>Register</h1>
            <br />
            {registerForm()}
            <br />
            {JSON.stringify(state)}
        </div>
    </Layout>
    );
};

export default Register;
