import {useState} from 'react';
import Layout from '../components/layout';
// import '../public/static/css/styles.css';


const Register = () => {

    // using hooks
    // array destructuring
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        // if two user try to register with same email then we will give error
        error: '',
        // after registering we will give success msg
        success: '',
        // we will change dynamically button text
        buttonText: 'Register Me'

    });

    // now we use directly like buttonText in form where buttn created
    const {name, email, password, error, success, buttonText} = state;

// using function inside another function e is an event
    const handleChange = (name) => (e) => {
        setState({...state, [name]: e.target.value, error: '', success: '', buttonText: 'Register Me'})
    };

const handleSubmit = e => {
    // to prevent reloading of page
    e.preventDefault();
    console.table(name, email, password);
}
    

    const registerForm = () => 
    <form onSubmit = {handleSubmit}>

<div class="col-md-7 register-right">
                        <h2>Register Here</h2>
                        <div class="register-form">
                            <div class="form-group">
                                <input type="text " class="input form-control" placeholder="Username"/>
                            </div>
                            <div class="form-group">
                                <input type="email " class="input form-control" placeholder="Email"/>
                            </div>
                            <div class="form-group">
                                <input type="password" class="input form-control" placeholder="Password"/>
                            </div>
                            <button type="button" class="btn btn-primary">Register</button>
                        </div>
                    </div>
    </form>

    return ( 
    <Layout>
        <div class="container">
        <div class="row">
            <div class="col-md-10 offset=md-1">
                <div class="row">
                    <div class="col-md-5 register-left">
                        <img src="/register.jpg" alt=""/>
                        <h3>Join Us</h3>
                        <p>Subscribe.......</p>
                       <button type="button" class="btn btn-primary">About</button> 
                    </div>
                    <div class="col-md-7 register-right">
                        <h2>Register Here</h2>
                        </div>
                    </div>
                    </div>
                    </div>
            <br />
            {registerForm()}
            <br />
            {JSON.stringify(state)}
        </div>
    </Layout>
    );
};

export default Register;
