import {useState} from 'react';
import Layout from '../components/layout';

const Register = () => {

    // using hooks
    // array destructuring
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        // if two user try to register with same email then we will give error
        error: '',
        // after registering we will give success msg
        success: '',
        // we will change dynamically button text
        buttonText: 'Register Me'

    });

    // now we use directly like buttonText in form where buttn created
    const {name, email, password, error, success, buttonText} = state;
// using function inside another function e is an event
    const handleChange = (name) => (e) => {
        setState({...state, [name]: e.target.value, error: '', success: '', buttonText: 'Register'})
    }

    con

    const registerForm = () => 
    <form onSubmit = {handleSubmit()}>
        <div className="form-group">
        <label for="">UserName</label>
        {/* by passing param. in ohandlehange func. we will know from where it is coming(from name or email...)  */}
            <input onChange={handleChange('name')} type="text" className="form-control" placeholder="Type Your name"/>
        </div>
        <div className="form-group">
        <label for="">E-mail</label>
            <input onChange={handleChange('email')} type="email" className="form-control" placeholder="Type Your email"/>
        </div>
        <div className="form-group">
        <label for="">Password</label>
            <input onChange={handleChange('password')} type="password" className="form-control" placeholder="Type Your password"/>
        </div>
        <div className="form-group">
          <button className="btn btn-outline-warning">{buttonText}</button>
        </div>
    </form>
    return ( 
    <Layout>
        <div className="col-md-6 offset-md-3">
            <h1>Register</h1>
            <br />
            {registerForm()}
        </div>
    </Layout>
    );
};

export default Register;
