// import head in nextjs
import Head from 'next/head'

// we can also use React.fragment instead of div

// destructuring children ("Hello Next from index.js")
const Layout = ({children}) => {
    
// now use head section
const Head = () => (
    <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css"></link>
)

    const nav = () => (
        <ul className="nav nav-tabs bg-secondary">
            <li className="nav-item"><a className="nav-link text-white" href="">Home</a></li>
            <li className="nav-item"><a className="nav-link text-white" href="">Login</a></li>
            <li className="nav-item"><a className="nav-link text-white" href="">Register</a></li>
        </ul>
    )
    return <React.Fragment>
        {/* we are executing nav here */}
       {Head()} {nav()} <div className="container pt-5 pb-5">{children}</div>
    </React.Fragment>
}

export default Layout;
